{"version":"V_2","layout":{"type":"column","children":[{"label":"控制板1","type":"block","source":"BLOCK","style":{"padding":"5px","margin":"5px"},"advance":{"expression":{}},"children":[{"type":"column","col":12,"children":[{"label":"控件组","type":"ctrlgroup","source":"CTRLGROUP","advance":{"expression":"expression = {
  on: {
    init: function (event) {
      var target = event.target;
      var global = event.global;
      var dItem,kpi;
      var subaccessConfigValues;
      var resource = target.getValue("global/resource");
      var today = target.dateHandler();
      var threeDayBefore = today.clone().addDay(-3);
      var dateRangeTime = [threeDayBefore, today];
      var success = function () {
        var ci = [resource.id]; // 设备 526443915616079
        var modelId = [resource.modelId] // 获取modelId
        var accessConfigValues = resource.physicalConfig.accessConfigs;
        target.getAttrsByModelId(modelId, function (attrs) {
          var MeasurePointLocate = attrs.find(function (elem) {
              return elem.name == "MeasurePointLocate";
            }) || {};
          var triggerEvent = function () {
              debugger;
            target.trigger("drawDiagnoseFeatureEchart1", {
              ins: dItem,
              kpi: kpi,
              dateRangeTime:dateRangeTime
            })
          }
          // 设备的属性表达式
           var sourceValue = eval(MeasurePointLocate.sourceValue)|| "";
           var sourceValueArr = [];
           for (var i in sourceValue) {
             for (var j in sourceValue[i]) {
               for(var k in sourceValue[i][j]){
                 sourceValueArr.push(sourceValue[i][j][k])
               }
             }
           };
          // 默认第一个选中
          dItem = dItem || sourceValueArr[0];
          subaccessConfigValues = accessConfigValues.filter(function(value){
            return value.instance == dItem.name;
          })
          kpi = subaccessConfigValues[0];
          triggerEvent();
          var render = function () {
            var ctrlGroups = [
              [{
                type: "label",
                value: "测点",
                class: "col-md-1",
                style: {
                  textAlign: "center"
                }
              }, {
                type: "select",
                value: dItem.name,
                class: "col-md-2",
                on: {
                  change: function (elem) {
                    dItem = elem.value;
                    subaccessConfigValues = accessConfigValues.filter(function(value){
                      return value.instance == dItem.name;
                    })
                    render();
                  }
                },
                options: sourceValueArr,
                format: {
                  "id": "name",
                  "label": "label"
                }
              }, {
                type: "label",
                value: "参数",
                class: "col-md-1",
                style: {
                  textAlign: "center"
                }
              }, {
                type: "select",
                value: kpi.kpiId,
                class: "col-md-2",
                on: {
                  change: function (elem) {
                     kpi = elem.value;
                     triggerEvent();
                  }
                },
                options: subaccessConfigValues,
                format: {
                  "id": "kpiId",
                  "label": "kpiName"
                },
              }, {
                type: "label",
                value: "起始时间",
                class: "col-md-1",
                style: {
                  textAlign: "center"
                }
              }, {
                type: "dateRangePicker",
                value: dateRangeTime.map(function(t){
                    return t.getDate();
                }),
                class: "col-md-3",
                on: {
                  change: function (elem) {
                      var startTime = target.dateHandler(elem.value.startTime);
                      var endTime = target.dateHandler(elem.value.endTime);
                    dateRangeTime =  [startTime, endTime];
                    triggerEvent();
                  }
                },
              }, {
                type: "button",
                class: "col-md-1",
                btnclass: "btn btn-primary",
                value: "设备信息",
                btnStyle: {
                  width: "100%",
                },
                on: {
                  click: function (elem) {
                  }
                }
              }, {
                type: "button",
                class: "col-md-1",
                btnclass: "btn btn-primary",
                value: "添加依据",
                btnStyle: {
                  width: "100%",
                },
                on: {
                  click: function (elem) {
                  }
                }
              }]
            ];
            target.render(ctrlGroups);
          };
          render();
        })
      }
      if(resource){
        success();    
      };
      target.on("tree_resourceChange", function (node) {
          debugger;
        success();
      })
    }
  }
}"},"style":{"margin":"5px","font-size":"12px"},"help":"../pdf/ctrlgroup.pdf","url":"images/map/map1.png","parameters":{}}]}],"url":"images/map/map1.png"},{"label":"高级视图","type":"advanceEchart","source":"ADVANCEECHART","advance":{"expression":"express = {
  on: {
    init: function (event) {
      var target = event.target;
      var global = event.global;
      var color = "#ffe400";
      var LENGTH = 50;
      var resource = target.getValue("global/resource");
      var time = 3 * 24 * 3600 * 1000;
      // 渲染echart图
      var renderEchart = function (ci, kpi, time,startTime,endTime) {
        // 扩展参数
        target.showLoading();
        target.hideNoData();
        var kpiNames =target.getValue("kpiName")||["温度"];
        var extension = {
          startTime:startTime,
          endTime:endTime
        };
        var xAxis = [];
        target.getKpiValueList(ci, kpi, time, function (returnData) {
          if(returnData.length > 0){
            target.hideLoading();
            var xAxisData = [];
            returnData.forEach(function (ele) {
              xAxisData.push(target.dateHandler(ele.arisingTime).getDateString("hh:mm:ss"));
            });
            var seriesData = []
            if (returnData) {
              for (var i in kpi) {
                var item = {
                  name: kpiNames[i],
                  data: returnData.map(function (elem) {
                    return elem.value;
                  })
                }
                seriesData.push(item);
              }
            }
            var option = {
              title: {
                textStyle: {color: "#fff"}
              },
              tooltip : {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'cross',
                        label: {
                            backgroundColor: '#6a7985'
                        }
                    }
                },
              legend: {
                data: seriesData.map(function (elem) {
                  return elem.name;
                })
              },
              xAxis: {
                boundaryGap: false,
                type: 'category',
                data: xAxisData
              },
              yAxis: {
                type: 'value'
              },
              dataZoom: [{
                show: true,
                realtime: true,
                start: 0,
                end: 10,
              }],
              series: seriesData.map(function (elem) {
                elem.type = "line";
                elem.symbolSize = 8;
                elem.data = elem.data;
                return elem;
              })
            };
            target.render(option);
          } else {
            target.showNoData();
          }

        }, extension)
      }
      debugger;
      target.off("drawDiagnoseFeatureEchart1");
      target.on("drawDiagnoseFeatureEchart1", function (condition) {
        var startTime = condition.dateRangeTime[0].getUTCDateString();
        var endTime = condition.dateRangeTime[1].getUTCDateString();
        var ci = [resource.id];
        var kpi = [condition.kpi.dataItemId];
        renderEchart(ci, kpi, time, startTime, endTime);
      });

    }
  }
};"},"style":{"margin":"auto","width":"100%","height":"500px"},"parameters":{"theme":"macarons"},"url":"images/map/map1.png"}],"col":12},"setting":"{
  "theme": "steel"
}"}

