{"version":"V_2","groups":[{"id":"0","label":"默认页","path":"index","layout":{"type":"column","children":[{"type":"row","source":"ROW","parameters":{"alignment":"bootstrap"},"style":{},"children":[{"type":"column","children":[{"label":"控制板1","type":"block","source":"BLOCK","style":{"padding":"5px","border":"0","min-height":"calc(100vh - 150px)","background":"rgba(0,0,0,.6)"},"advance":{"expression":{}},"children":[{"type":"column","col":12,"children":[{"label":"控件组","type":"ctrlgroup","source":"CTRLGROUP","advance":{"expression":"expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var kpiOption = [{
          id : 1002,
          label : "报警数量"
        },{
          id : 1003,
          label : "报警准确率"
        },{
          id : 1004,
          label : "判断及时率"
        },{
          id : 1005,
          label : "诊断准确率"
        }];
        var valKpi = [kpiOption[0]];
        target.setValue("decider/kpi", valKpi);
        var render = function(){
          var ctrlGroups = [
            [{
              type : "label",
              value : "指标"
            }],
            [{
              type : "checkboxlist",
              multiselect : false,
              value : valKpi.map(function(elem){
                return elem.id;
              }),
              on : {
                change : function(elem){
                  valKpi = elem.value;
                  target.setValue("decider/kpi", valKpi);
                  render();
                }
              },
              options : kpiOption
            }]
          ];
          target.render(ctrlGroups);
        }
        render();
