          return elem.randomText(['系统自动']);
        },
        tech : function(inx, elem, attr){
          return elem.randomText(['振动监测']);
        },
        status : function(inx, elem, attr){
          return elem.randomText(['额定工况']);
        },
        abnormal :  function(inx, elem, attr){
          return elem.randomText(['振动值超标，轴承存在异音', '风机垂直振动值严重超标', '传动轴轴承座振动超标']);
