                    target.navigateTo("device", {
                      tabLabel : elem.label
                    });

                  }
                }
              }],
              render : function(){
                return ""
              }
            });
            target.render({
              data : filter,
              on : {
                rowClick : function(event){
                  target.createPopupBypath('analysis', {
                    title: "诊断分析",
                    theme : "system",
