        target.getSimulateApi(simulate, 10, function(datas){
          var render = function(){
            var format = [];
            for(var i in Dictionary){
              var item = target.explainDic(i, Dictionary[i]);
              format.push(item);
            }
            var search = target.getScopeValue("search");
            var filter = datas.filter(function(elem){
              for(var i in search){
                var type = search[i].type;
                var value = search[i].value;
                if(type == "between"){
                  if(value){
                    var dt = target.dateHandler(elem[i]);
                    var dt0 = value[0];
                    var dt1 = value[1];
                    var rs = (dt0 ? dt.after(dt0) : true) && (dt1 ? dt.before(dt1) : true);
                    if(!rs){
                      return false;
                    };
                  } else {
                    return true;
                  }

                } else if(type == "inside"){
                  var some = value.some(function(el){
                    return elem[i] == el.title || elem[i] == el.label;
                  });
                  if(!some){
                    return false;
                  }
                } else if(type == "domains"){
                  return elem.domains.indexOf(value.domains) != -1;
                }
              };
              return true;
            })
            format.push({
              name : "处理",
              type: "buttonGroup",
              content : [{
                label : "详情",
                icon: "null",
                class: "btn btn-default btn-sm",
                on : {
                  click : function(elem){
                    target.navigateTo("device", {
                      tabLabel : elem.label
                    });

                  }
                }
              }],
              render : function(){
                return ""
              }
            });
            target.render({
              data : filter,
              on : {
                rowClick : function(event){
                  target.createPopupBypath('analysis', {
                    title: "诊断分析",
                    theme : "system",
                    width: "80%"
                  }, {
                    message: "诊断分析"
                  })
                }
              },
              format : format
            });
          };
          target.off("search_toDolist");
          target.on("search_toDolist", function(){
            render();
          });
          render();
        });
      })
    }
  }
}"},"style":{"margin":"5px","font-size":"12px"},"url":"images/map/map1.png","echart":{}}]}],"url":"images/map/map1.png"}],"col":12},"setting":"{
