const {
  getFilePath
} = require("ps-ultility");
let {
  expect
} = require("chai");
let getFileStateInstance = require("../../dist/lib/file-state.js"),
  workPath = process.cwd(),
  filePath = getFilePath(__filename);
describe("测试模块 FileState :", () => {
  describe("模块【FileState】成功导入 :", () => {
    it("成功引入./file-state.js", () => {
      expect(typeof getFileStateInstance).to.be.equal("function");
    })
  })
  describe("模块【FileState】初始化 :", () => {
    let fileState = getFileStateInstance({
      "workPath": workPath,
      "filePath": filePath
    });
    it("成功引入./file-state.js", () => {
      expect(typeof fileState).to.be.equal("object");
    })
    it(`file-state.getFiles("core")`)
  })
})